import { Typography, MenuItem, TextField,Box, Grid,Button } from "@mui/material";
import { useState } from "react";
import { ThemeProvider } from '@emotion/react';
import theme from "../ui/Theme";
import "./SubjectDetailPage.css";

import AddIcon from '@mui/icons-material/Add';

const weekSubject = [
  {
    number: 1,
    date: "12/12/21",
  },
  {
    number: 2,
    date: "24/12/21",
  },
];

export default function SubjectDetail() {
    const [tab, setTab] = useState(1);

    const changeTab = (index) => {
      setTab(index);
    };
    return (
        <div style={{padding:"2%",width:'90%'}}>
        <Typography variant="h2">Python</Typography>
        <Typography variant="subtitle2">Professor : Alex</Typography>
        <Typography variant="subtitle2">
          Class time : 13:00 PM - 15.00 PM
        </Typography>
        <Typography variant="subtitle2">
          Mid-term examination : 30 Feb 2035, 13.00 AM - 15.00 PM
        </Typography>
        <Typography variant="subtitle2">
          Final examination : 30 April 2035, 13.00 AM - 15.00 PM
        </Typography>
        <br></br>
        <div>
  <div className="block_tab">
          <div
            onClick={() => changeTab(1)}
            className={tab === 1 ? "tabs active-tabs" : "tabs"}
          >
            Materials
          </div>
          <div
            onClick={() => changeTab(2)}
            className={tab === 2 ? "tabs active-tabs" : "tabs"}
          >
            Assignments{" "}
          </div>
          <div
            onClick={() => changeTab(3)}
            className={tab === 3 ? "tabs active-tabs" : "tabs"}
          >
            Notes
          </div>
          {/* <div className="drop_down" sx={{borderRadius:"10px"}}>
            <TextField select borderRadius = "29">
              {weekSubject.map((week) => (
                <MenuItem key={week.number} value={week.number}>
                  {"Week " + week.number + "," + week.date}
                </MenuItem>
              ))}
            </TextField>
          </div> */}
        </div>
        <div className="content_container">
        <Box sx={{display:"flex"}}>
        <Button style={{backgroundColor:"green", margin:"1% 0%",marginLeft:"auto"}}variant="contained" startIcon={<AddIcon/>}>
                Add New Material
            </Button>
          </Box>
        <Grid container columns={{ xs: 4, sm: 8, md: 12 }} spacing={2}>
            {Array.from(Array(6)).map((_, index) => (
                <Grid item xs={2} sm={4} md={4} key={index}>
                <Box sx={{backgroundColor:"primary.dark", borderRadius:"5%", height:"20vh",padding:"5%"}}>
                <Typography variant="h1" fontSize="1rem" color="secondary.main">Slide 1 : Variable</Typography>
                <Typography variant="h3" fontSize="0.5rem" color="secondary.main">Variables are used to store information to be referenced and manipulated in a computer program. 
                They also provide a way of labeling data with a descriptive name</Typography>
                </Box>

        </Grid>
        ))}
        </Grid>
        </div>
        </div>
           {/* Dialog */}
      <Dialog
        open={dialogOpen}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleDialogClose}
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle sx={{textAlign:"center"}}>{"Add Subject to your room"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-slide-description">
          <AddSubjectPopUp/>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose}>Cancel</Button>
          <Button onClick={handleDialogClose}>Submit</Button>
        </DialogActions>
      </Dialog>

        </div>
    );
  }
