import React, { useEffect } from 'react'
import { ChatState } from '../Context/ChatProvider'
import Box from '@mui/material/Box'
import { Typography, FormControl, Input } from '@mui/material';
import {getSender} from "../Config/ChatLogics"
import UpdateGroupChatModal from './UpdateGroupChatModal';
import CircularProgress from '@mui/material/CircularProgress';
import {useState} from 'react'
import axios from 'axios'
import ScrollableChat from './ScrollableChat'

import ChatBubbleIcon from '@mui/icons-material/ChatBubble';

import { ThemeProvider } from '@emotion/react';
import theme from "../ui/Theme";
// import io from 'socket.io-client'

const SingleChat = ({fetchAgain, setFetchAgain}) => {

    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [newMessage, setNewMessage] = useState();
    const [socketConnected, setSocketConnected] = useState(false)
    const [typing, setTyping] = useState(false);
    const [istyping, setIsTyping] = useState(false);

    const { user, selectedChat, setSelectedChat} = ChatState();

    const fetchMessages = async () => {
        if (!selectedChat) return;

        try {
          const config = {
            headers: {
              Authorization: `Bearer ${user.token}`,
            },
          };

          setLoading(true);

          const { data } = await axios.get(
            `/api/message/${selectedChat._id}`,
            config
          );

          setMessages(data);
          setLoading(false);


        } catch (error) {

        }
      };

    const sendMessage = async(event) => {
        if(event.key==="Enter" && newMessage){
            try {
                const config = {
                    headers: {
                        "Content-Type":"application/json",
                        Authorization: `Bearer ${user.token}`,
                    }
                }
                setNewMessage("");
                const { data } = await axios.post('/api/message', {
                    content: newMessage,
                    chatId: selectedChat._id
                },
                config

                )
                console.log(data)

                setMessages([...messages, data]);
            } catch (error) {
                console.log(error)
            }
        }}
    const typingHandler = (e) => {
        setNewMessage(e.target.value)
    }

    useEffect(() => {
            fetchMessages();
    }, [selectedChat])

    return (
        <ThemeProvider theme={theme}>
 <div style={{flexGrow:"1", padding:"20px 40px", maxWidth:"100%"}}>
        {selectedChat ? (
            <>
        <Box paddingBottom="15px" sx={{borderBottom:"1px solid #112D4E"}}>

        <Typography
        fontSize={{ base: "28px", md: "30px" }}
        w="100%"
        alignItems="center"
      >
          {!selectedChat.isGroupChat ? (
              <>
                {getSender(user,selectedChat.users)}
                {/* profilemodal */}
              </>
          ) : (
              <>
              {selectedChat.chatName.toUpperCase()}
              </>
          )}
      </Typography>
        </Box>


        <Box 
        sx={{
        d:"flex",
        flexDir:"column",
        justifyContent:"flex-end",
        backgroundColor:"secondary.main",
        w:"100%",
        h:"100%",
        overflowY:"hidden",
        }}
        >
        {loading ? (
            <div style={{height:'500px',display: 'flex', justifyContent: 'center', marginTop:"50%"}}>
                <CircularProgress/>
            </div>
            ):(
            <div className="messages" style={{overflowY:'scroll',height:"500px"}}>
                <ScrollableChat messages={messages}/>
            </div>
            )}
            <Box sx={{position:"absolute", bottom:"0"}}>
            <FormControl onKeyDown={sendMessage} isRequire>
                {istyping ? <div>Loading...</div> : <></>}
                <Input
                    // variant="filled"
                    backgroundColor="#E0E0E0"
                    placeholder="Enter a message.."
                    onChange={typingHandler}
                    value={newMessage}
                />
            </FormControl>
            </Box>
          </Box>
            </>
        ) : (
            //No DM
            <Box d="flex" alignItems="center" justifyContent="center" h="100%" >
                <Typography fontFamily="Work sans" fontSize="20px" j>
                    Click on a user to start chatting
                </Typography>
            </Box>
        )}
    </div>
        </ThemeProvider>
    )
}

export default SingleChat;
