import { Card, CardContent, CardActions,Stack, Box, Typography, Divider,Button, Avatar} from "@mui/material";
import { ThemeProvider } from '@emotion/react';
import theme from "../ui/Theme";
import SearchBar from "../Components/SearchBar.js";
import AddIcon from '@mui/icons-material/Add';

import PersonRemoveIcon from '@mui/icons-material/PersonRemove';
import ChatIcon from '@mui/icons-material/Chat';

import {ChatState} from "../Context/ChatProvider"

const member = [
    {
      key: 1,
      Avatar: "",
      Name: "Pure",
      role:"Admin"
    },
    {
      key: 2,
      Avatar: "",
      Name: "Pond",
      role:"Admin"
    },
    {
      key: 3,
      Avatar: "",
      Name: "Thri",
      role:"Member"
    },
  ];

export default function MemberPage(){
    return(
   <ThemeProvider theme={theme}>
        <Box sx={{padding:"1%"}}> 
        <Box textAlign="center">
        <Typography variant="h2" fontSize="1.25rem">Members</Typography>
        </Box>    
        <Stack direction="row"> 
        </Stack>
      <Divider/>
      <Box sx={{marginBottom:"1%"}}>
      <SearchBar/>
      </Box>
    {member.map((m) => (
        <Box sx={{ border: "1px solid grey", borderRadius: "3px", alignItems: "center", display: "flex", flexWrap: "wrap", padding: "2%", cursor: 'pointer', marginBottom:"0.5%"}}>
              <Avatar sx={{marginRight:"1rem"}}></Avatar>
              <Stack direction="column" spacing={-2}>
              <Typography variant='overline' fontSize="1rem">{m.Name}</Typography>
              <Typography>{m.role}</Typography>
              </Stack>
              <div style={{marginLeft:"auto"}}>
              <Button style={{backgroundColor:"primary.main"}}variant="contained" startIcon={<ChatIcon/>}>
                Direct Message
            </Button>
            <Button style={{backgroundColor:"red",marginLeft:"0.2rem"}}variant="contained" startIcon={<PersonRemoveIcon/>}>
               Remove from room
            </Button>
              </div>

            </Box>
      ))}
        </Box>
        </ThemeProvider>
    )
}
