import React, { useState, useEffect} from 'react'
import {ChatState} from "../Context/ChatProvider"
import axios from "axios";
import {Paper, Typography, Avatar} from "@mui/material"
import Box from "@mui/material/Box";
import List from "@mui/material/List";
import ChatLoading from "./ChatLoading"
import {getSender} from "../Config/ChatLogics"
import Button from '@mui/material/Button'
import Stack from "@mui/material/Stack"


 const MyChats = () => {
    const [loggedUser, setLoggedUser] = useState();
    const { selectedChat, setSelectedChat, user, chats, setChats} = ChatState();

    const fetchChats = async () => {
        // console.log(user._id);
        try {
          const config = {
            headers: {
              Authorization: `Bearer ${user.token}`,
            },
          };

          const { data } = await axios.get("/api/chat", config);

          console.log(data);

          setChats(data);
        } catch (error) {
          console.log("Error fetching chats")
        }
      };
      useEffect(() => {
        setLoggedUser(JSON.parse(localStorage.getItem("userinfo")));
        fetchChats();

      }, []);

    return (

                        // <Box
                        // backgroundColor="#F8F8F8"
                        // borderRadius="1px"
                        // overflowY="hidden"
                        // padding={3}
                        // borderRadius="5px"
                        // alignItems="center"
                        // height="100%"
                        // weidth="100%"
                        // >
                        // { chats ? (
                        //     <Paper style = {{ maxHeight: 300, overflow: 'auto'}} >
                        //         {chats.map((chat) => (
                        //             <List
                        //             onClick={() => setSelectedChat(chat)}
                        //             cursor="pointer"
                        //             backgroundColor = {selectedChat === chat ? "#38B2AC" : "#E8E8E8"}
                        //             color = {selectedChat === chat ? "white" : "black"}
                        //             py={2}
                        //             borderRadius="5px"
                        //             key={chat._id}
                        //             >
                        //                 <Typography>
                        //                     {!chat.isGroupChat ? getSender(loggedUser, chat.users):
                        //                     chat.chatName}
                        //                 </Typography>
                        //             </List>
                        //         ))}
                        //     </Paper>
                        // ) : (
                        //     <ChatLoading/>
                        // )}

                        // </Box>

                    // </Box>

                    <Box sx={{ backgroundColor:"primary.main", alignItems: "center", display: "flex", flexWrap: "wrap", padding: "2%", cursor: 'pointer'}}>
              {/* <Avatar sx={{marginRight:"2%"}}>SP</Avatar>
              <Typography variant='subtitles' fontSize="1rem" color="secondary.main">Pure</Typography> */}
               { chats ? (

                            <Stack style = {{ maxHeight: 500, overflow: 'auto'}} fontSize="1rem" >
                                {chats.map((chat) => (
                                    <List
                                    onClick={() => setSelectedChat(chat)}
                                    cursor="pointer"
                                    backgroundColor = {selectedChat === chat ? "#38B2AC" : "#E8E8E8"}
                                    color = {selectedChat === chat ? "white" : "black"}
                                    py={2}
                                    borderRadius="5px"
                                    key={chat._id}
                                    >
                                      <Avatar
                                    sx={{marginRight:"2%"}}
                                    size="sm"
                                    cursor="pointer"
                                    name={user.name}
                                    src={user.pic}
                                    />
                                        <Typography>
                                            {!chat.isGroupChat ? getSender(loggedUser, chat.users):
                                            chat.chatName}
                                        </Typography>
                                    </List>
                                ))}
                            </Stack>
                        ) : (
                            <ChatLoading/>
                        )}
            </Box>
    )
}

export default MyChats;
