import React from 'react'
import {useState} from 'react'
import Button from "@mui/material/Button"
import { Typography, Drawer, Input,FormControl, IconButton } from '@mui/material'
import Box from "@mui/material/Box";
import { ChatState} from "../Context/ChatProvider"
import ChatLoading from "./ChatLoading"
import UserListItem from "./UserListItem"
import axios from 'axios'

import SearchIcon from '@mui/icons-material/Search';

import { ThemeProvider } from '@emotion/react';
import theme from "../ui/Theme";



 const SlideDrawer = () => {
    const [search, setSearch] = useState("")
    const [searchResult, setSearchResult] = useState([])
    const [open, setOpen] = useState(false)
    const [loading,setLoading] = useState(false)
    const [loadingChat, setLoadingChat] = useState();
    const handleClose = () => setOpen(false)

    const { user, setSelectedChat, chats, setChats} = ChatState();
    const toggleDrawer = (newOpen) => (event) => {
        setOpen(newOpen)
    }

    const handleSearch = async() => {
        if(!search){
            console.log("error no search entered")
        }

        try{

            const config = {
                headers: {

                    Authorization: `Bearer ${user.token}`,
                }
            };
            const { data} = await axios.get(`/api/user?search=${search}`, config)


            setLoading(false);
            setSearchResult(data);

        }catch (error){
            console.log("error can't fetch data");
        }
    }

    const accessChat = async (userId) => {
        console.log(userId);

        try {
          setLoadingChat(true);

          const config = {
            headers: {
              "Content-type": "application/json",
              Authorization: `Bearer ${user.token}`,
            },
          };

          const { data } = await axios.post(`/api/chat`, { userId }, config);

          if (!chats.find((c) => c._id === data._id)) setChats([data, ...chats]);

          setSelectedChat(data);
          setLoadingChat(false);

          handleClose(false);
        } catch (error) {

        }
      };

    return (
      <ThemeProvider theme={theme}>
  <div>
            <Button onClick={toggleDrawer(true)} sx={{backgroundColor:"primary.main"}}>
                {/* <i class="fas fa-search"></i> */}
                <Typography variant="h2" fontSize="1.25em" color="secondary.main" px={2}>Make some conversation</Typography>
            </Button>

          <Drawer
            anchor={"left"}
            open={open}
            onClose={toggleDrawer(false)}
            onOpen={toggleDrawer(true)}
          >
            {/* <Box d="flex" pb={2}>
                <Typography variant="h5">Search user</Typography>
                <input
                placeholder="Type..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                >
                </input>
                <Button onClick={handleSearch} variant="ghost"> Go </Button>
            </Box> */}
            <Box sx={{
                backgroundColor: "#fff",display: "flex", justifyContent: "space-between",padding: "10px",borderRadius: "8px"
            }}> 
            <FormControl isRequire>
            <Input
                    placeholder="Who would you like to communicate ?"
                    onChange={(e) => setSearch(e.target.value)}
                    value={search}
                    style={{flexGrow:"1", padding:"0 15px", backgroundColor:"transparent",border:"none",outline:"none"}}
                    disableUnderline="true"
                />
            </FormControl>
            <IconButton sx={{
                    width:"36px", height:"36px", backgroundColor:"primary.main",color:"secondary.main", cursor:"pointer"
                    ,borderRadius:"5px", lineHeight:"36px",
                    transition:"all 0.3s cubic-bezier(0.88, 0.19, 0.37, 1.11)"
                }}>
                <SearchIcon/>
                </IconButton>
            </Box>
            {loading ? (
              <ChatLoading />
            ) : (
              searchResult?.map((user) => (
                <UserListItem
                  key={user._id}
                  user={user}
                  handleFunction={() => accessChat(user._id)}
                />
              ))
            )}
          </Drawer>
        </div>
      </ThemeProvider>

    )
}

export default SlideDrawer
